  },
  "take_profit": {
    "type": "number"
  },
  "risk_assessment": {
    "risk_level": {
      "type": "string",
      "enum": [
        "low",
        "medium",
        "high"
      ]
    },
    "risk_score": {
      "type": "number",
      "minimum": 0,
      "maximum": 1
    }
  },
  "confidence": {
    "type": "number",
    "minimum": 0,
    "maximum": 1
  },
  "rationale": {
    "type": "string"
  }
}

